function update_answer(){switch(window.location.hash){case"#nej":answer_yes.style.display="none",answer_no.style.display="";break;case"#ja":answer_no.style.display="none",answer_yes.style.display=""}}function random_answer(){question_button.blur(),update_hash(Math.random()>.66666?"#ja":"#nej")}function update_hash(e){window.location=e}var answer_no=document.querySelector("#answer-no"),answer_yes=document.querySelector("#answer-yes"),question_button=document.querySelector("#question-button");answer_no.style.display=answer_yes.style.display="none",window.addEventListener("hashchange",update_answer),question_button.addEventListener("click",random_answer),update_answer();